const result = [];
for (const item of $input.all()) {
  result.push(...item.json.data.news);
}
const resultWithKeyword = result.map(item => ({
  ...item,
  keyword: $('Split Out2').first().json.keyword
  }));
return resultWithKeyword;

const inputString = $input.first().json.urls.replace(/\r\n/g, '');

// 使用 split(',') 方法按逗号分割字符串，生成数组
const urlArray = inputString.split(',');

// 返回一个新的 item，包含字符串数组（你可以根据需要调整输出字段名）
return [
  {
    json: {
      urls: urlArray,  // 输出示例: ["https://www.reuters.com/technology/artificial-intelligence/", "https://news.aibase.cn/news"]
      keyword: $input.first().json.keyword
    }
  }
];

// 处理多种日期格式并对比
function processDate(inputDate) {
  // 如果是"刚刚"等特殊字符串，直接返回今天
  if (typeof inputDate !== 'string' || inputDate.includes('刚刚') || inputDate.includes('刚才')) {
    return new Date();
  }

  // 移除可能的时间部分和时区信息
  let dateStr = inputDate.split('T')[0].split(' ')[0];

  // 统一分隔符为破折号
  dateStr = dateStr.replace(/\//g, '-');

  // 解析日期
  let parsedDate;

  try {
    // 尝试直接解析
    parsedDate = new Date(dateStr);

    // 如果解析失败，尝试手动解析
    if (isNaN(parsedDate.getTime())) {
      const parts = dateStr.split('-');
      if (parts.length === 3) {
        const year = parseInt(parts[0]);
        const month = parseInt(parts[1]) - 1; // 月份从0开始
        const day = parseInt(parts[2]);
        parsedDate = new Date(year, month, day);
      }
    }
  } catch (error) {
    // 如果所有解析都失败，返回无效日期
    parsedDate = new Date('invalid');
  }

  return parsedDate;
}

// 主处理逻辑
const inputDate = $input.first().json.date; // 假设日期字段名为 date
const yesterday = new Date();
yesterday.setDate(yesterday.getDate() - 1);
yesterday.setHours(0, 0, 0, 0); // 设置为昨天开始时间

const targetDate = processDate(inputDate);

if (isNaN(targetDate.getTime())) {
  // 日期解析失败的处理
  return { 
    isValid: false,
    isAfterYesterday: false,
    originalDate: inputDate,
    error: '无法解析日期格式'
  };
} else {
  const isAfterYesterday = targetDate >= yesterday;

  return {
    isValid: true,
    isAfterYesterday: isAfterYesterday,
    originalDate: inputDate,
    parsedDate: targetDate.toISOString(),
    yesterday: yesterday.toISOString(),
    link: $input.first().json.link,
    title: $input.first().json.title,
    source: $input.first().json.source,
    date: $input.first().json.date,
    keyword: $input.first().json.keyword
  };
}

