const voice_timbre_list = $('配置字段').first().json.voice_script.voice_scripts.map(item => {
  const voice = $input.first().json.output.data.find(d => d.timbre_id === item.timbre_id)
  return {
    ...item,
    "voice_id": voice.voice_id 
  }
})

return {
  "voice_timbre_list": voice_timbre_list
}

function getFlacDurationFromBinary(binaryData) {
    try {
        // 从 n8n 的 binary 数据获取 Buffer
        const buffer = Buffer.from(binaryData.data, 'base64');

        // 验证 FLAC 文件头
        if (buffer.toString('utf8', 0, 4) !== 'fLaC') {
            return { error: '不是有效的 FLAC 文件' };
        }

        let offset = 4;

        // 读取元数据块
        while (offset < buffer.length) {
            const blockHeader = buffer.readUInt32BE(offset);
            offset += 4;

            const isLast = (blockHeader & 0x80000000) !== 0;
            const blockType = (blockHeader & 0x7F000000) >>> 24;
            const blockSize = blockHeader & 0x00FFFFFF;

            // STREAMINFO 块类型是 0
            if (blockType === 0) {
                // 采样率在偏移 10-12 (20位)
                const sampleRateHigh = buffer.readUInt16BE(offset + 10);
                const sampleRateLow = buffer.readUInt8(offset + 12);
                const sampleRate = ((sampleRateHigh << 4) | (sampleRateLow >> 4)) & 0xFFFFF;

                // 总样本数在偏移 13-17 (36位)
                const totalSamplesBytes = buffer.slice(offset + 13, offset + 18);
                const totalSamples =
                    ((totalSamplesBytes[0] & 0x0F) * 0x100000000) +
                    (totalSamplesBytes[1] * 0x1000000) +
                    (totalSamplesBytes[2] * 0x10000) +
                    (totalSamplesBytes[3] * 0x100) +
                    totalSamplesBytes[4];

                if (sampleRate > 0) {
                    const duration = totalSamples / sampleRate;
                    const minutes = Math.floor(duration / 60);
                    const seconds = Math.floor(duration % 60);

                    return {
                        duration_seconds: duration,
                        duration_formatted: `${minutes}:${seconds.toString().padStart(2, '0')}`,
                        sample_rate: sampleRate,
                        total_samples: totalSamples
                    };
                }
                break;
            }

            offset += blockSize;
            if (isLast) break;
        }

        return { error: '未找到 STREAMINFO 块' };

    } catch (error) {
        return { error: error.message };
    }
}

// 从上一个节点获取 binary 数据
const binaryData = $input.first().binary.data;

const result = getFlacDurationFromBinary(binaryData);

// 返回结果
return {
  duration_ms: Math.floor(result.duration_seconds * 1000)
};
