parameters.jsonBody:
={
  "urls": ["{{ $json.url }}"],
  "prompt": "Extract the latest news related to AI, including the title, date, link, and source of each news item.",
  "schema": {
    "type": "object",
    "properties": {
      "news": {
        "type": "array",
        "items": {
          "type": "object",
          "properties": {
            "title": {
              "type": "string"
            },
             "link": {
              "type": "string"
            },
            "date": {
              "type": "string"
            },
            "source": {
              "type": "string"
            }
          },
          "required": [
            "title",
            "link",
            "date"
          ]
        }
      }
    },
    "required": [
      "news"
    ]
  },
  "enableWebSearch": false
}

parameters.inputText:
={
  "instruction": "基于以下分类标准分析文本：商业动态、投融资、生态合作。如不匹配则归为生态合作。",
  "text": "{{ $json.data.markdown }}"
}

parameters.jsCode:
const result = [];
for (const item of $input.all()) {
  result.push(...item.json.data.news);
}
return result;
