let article = "";

// Introduction
article += $('文章结构').first().json.output.introduction;

for (const item of $input.all()) {
  article += "<strong>" + item.json.title + "</strong>";
  article += item.json.output;
  article += "<br>";
}

// Conclusions
article += "<strong> Conclusions </strong>";
article += $('文章结构').first().json.output.conclusions;


return [
  {
    "article": article
  }
];

