parameters.jsCode:
// 获取所有输入项
let items = $input.all();

if (items.length === 0) {
  return [{ json: { error: "没有输入数据" } }];
}

// 定义章节的显示顺序
const chapterOrder = [
  "市场资讯",
  "宏观资讯", 
  "通信ICT行业资讯",
  "商业观察"
];

const sectionOrder = [
  "国外",
  "国内", 
  "港澳地区",
  "商业动态",
  "投融资",
  "生态合作"
];

// 按chapter和section分组数据
const groupedData = {};

items.forEach(item => {
  const chapter = item.json.chapter;
  const section = item.json.section;
  const content = item.json.content || "";

  if (!groupedData[chapter]) {
    groupedData[chapter] = {};
  }

  if (!groupedData[chapter][section]) {
    groupedData[chapter][section] = [];
  }

  // 直接存储完整的content内容
  if (content.trim()) {
    groupedData[chapter][section].push(content.trim());
  }
});

// 生成文章内容
let article = "";

chapterOrder.forEach((chapter, chapterIndex) => {
  if (!groupedData[chapter]) return;

  // 检查该章节下是否有任何内容
  let hasContent = false;
  sectionOrder.forEach(section => {
    if (groupedData[chapter][section] && groupedData[chapter][section].length > 0) {
      hasContent = true;
    }
  });

  // 如果章节没有任何内容，跳过该章节
  if (!hasContent) return;

  // 添加一级标题
  const chapterNumber = chapterIndex + 1;
  article += `# ${chapterNumber} ${chapter}\n`;

  // 添加章节描述（根据章节类型）
  const descriptions = {
    "市场资讯": "重大资讯",
    "宏观资讯": "仅限重大政策，政治新闻，国家层面、政府部门、管理机构如：工信部等发布的政策、条例等", 
    "通信ICT行业资讯": "仅限运营商及ICT行业",
    "商业观察": "侧重相关商业动态，投融资、生态合作等相关资讯"
  };

  if (descriptions[chapter]) {
    article += `${descriptions[chapter]}\n\n`;
  }

  // 添加二级标题和内容
  let sectionCount = 0;
  sectionOrder.forEach(section => {
    if (!groupedData[chapter][section] || groupedData[chapter][section].length === 0) return;

    const sectionNumber = `${chapterNumber}.${sectionCount + 1}`;
    article += `## ${sectionNumber} ${section}\n\n`;

    // 添加该section下的所有完整内容
    groupedData[chapter][section].forEach(content => {
      article += `${content}\n\n`;
    });

    sectionCount++;
  });

  article += "\n";
});

// 返回结果
return [{
  json: {
    formattedArticle: article
  }
}];

